 */

/**
 * 将图片转换为JPEG格式并压缩
 * @param {File} file - 原始图片文件
 * @param {number} quality - 压缩质量，范围0-1，默认0.8
 * @param {Function} onProgress - 进度回调函数
 * @returns {Promise<File>} - 处理后的图片文件
 */
export const convertAndCompressImage = (file, quality = 0.8, onProgress) => {
  return new Promise((resolve, reject) => {
    // 报告进度：开始
    if (onProgress) onProgress(0);
    console.log('开始处理图片:', file.name);
    console.log('原始文件类型:', file.type);
    console.log('原始文件大小:', (file.size / 1024).toFixed(2) + 'KB');

    // 检查文件类型
    if (!file || !file.type.startsWith('image/')) {
      reject(new Error('无效的图片文件'));
      return;
    }

    // 如果已经是JPEG格式且小于2MB，可以直接返回
    if (file.type === 'image/jpeg' && file.size < 2 * 1024 * 1024) {
      console.log('文件已经是JPEG格式且小于2MB，跳过处理');
      if (onProgress) onProgress(100);
      resolve(file);
      return;
    }
    const reader = new FileReader();
    reader.onload = e => {
      // 报告进度：25%
      if (onProgress) onProgress(25);
      const img = new Image();
      img.onload = () => {
        try {
          // 报告进度：50%
          if (onProgress) onProgress(50);
          console.log('图片加载成功，原始尺寸:', img.width, 'x', img.height);

          // 计算合适的尺寸，避免Canvas尺寸过大
          let width = img.width;
          let height = img.height;

          // 如果图片尺寸过大，按比例缩小
          const MAX_WIDTH = 2048;
          const MAX_HEIGHT = 2048;
          if (width > MAX_WIDTH || height > MAX_HEIGHT) {
            if (width > height) {
              height = Math.round(height * (MAX_WIDTH / width));
              width = MAX_WIDTH;
            } else {
              width = Math.round(width * (MAX_HEIGHT / height));
              height = MAX_HEIGHT;
            }
            console.log(`图片尺寸过大，已调整为 ${width}x${height}`);
          }

          // 创建canvas
          const canvas = document.createElement('canvas');
          canvas.width = width;
          canvas.height = height;

          // 在canvas上绘制图片
          const ctx = canvas.getContext('2d');
          ctx.fillStyle = '#FFFFFF'; // 设置白色背景
          ctx.fillRect(0, 0, width, height);
          ctx.drawImage(img, 0, 0, width, height);

          // 报告进度：75%
          if (onProgress) onProgress(75);
          console.log('开始将Canvas转换为JPEG Blob，质量:', quality);

          // 将canvas转换为blob，使用JPEG格式
          canvas.toBlob(blob => {
            if (!blob) {
              console.error('Canvas转换为Blob失败');
              reject(new Error('图片转换失败'));
              return;
            }
            console.log('Blob创建成功，类型:', blob.type);
            console.log('Blob大小:', (blob.size / 1024).toFixed(2) + 'KB');
            try {
              // 创建新的File对象，使用.jpg扩展名和JPEG MIME类型
              const newFileName = file.name.replace(/\.[^/.]+$/, '') + '.jpg';
              const processedFile = new File([blob], newFileName, {
                type: 'image/jpeg',
                lastModified: Date.now()
              });

              // 报告进度：100%
              if (onProgress) onProgress(100);

              // 输出压缩信息
              console.log('原始文件大小:', (file.size / 1024).toFixed(2) + 'KB');
              console.log('压缩后文件大小:', (processedFile.size / 1024).toFixed(2) + 'KB');
              console.log('压缩率:', ((1 - processedFile.size / file.size) * 100).toFixed(2) + '%');
              console.log('处理后的文件名:', processedFile.name);
              console.log('处理后的文件类型:', processedFile.type);
              resolve(processedFile);
            } catch (error) {
              console.error('创建File对象失败:', error);
              reject(new Error('创建File对象失败: ' + error.message));
            }
          }, 'image/jpeg',
          // 使用JPEG格式
          quality // 使用传入的质量参数
          );
        } catch (error) {
          console.error('Canvas处理图片失败:', error);
          reject(new Error('Canvas处理图片失败: ' + error.message));
        }
      };
      img.onerror = error => {
        console.error('图片加载失败:', error);
        reject(new Error('图片加载失败'));
      };

      // 设置图片源
      img.src = e.target.result;
    };
    reader.onerror = error => {
      console.error('文件读取失败:', error);
      reject(new Error('文件读取失败'));
    };

    // 读取文件为DataURL
    reader.readAsDataURL(file);
  });
};

/**
 * 批量处理图片文件
 * @param {File[]} files - 图片文件数组
 * @param {number} quality - 压缩质量，范围0-1，默认0.8
 * @param {Function} onProgress - 进度回调函数
 * @returns {Promise<File[]>} - 处理后的图片文件数组
 */
export const batchProcessImages = async (files, quality = 0.8, onProgress) => {
  if (!files || files.length === 0) {
    return [];
  }
  const totalFiles = files.length;
  const processedFiles = [];
  for (let i = 0; i < totalFiles; i++) {
    const file = files[i];
    try {
      // 计算当前文件的进度范围
      const startProgress = i / totalFiles * 100;
      const endProgress = (i + 1) / totalFiles * 100;
      const processedFile = await convertAndCompressImage(file, quality, progress => {
        // 将单个文件的进度映射到总体进度范围
        const mappedProgress = startProgress + progress / 100 * (endProgress - startProgress);
        if (onProgress) onProgress(Math.round(mappedProgress));
      });
      processedFiles.push(processedFile);
    } catch (error) {
      console.error(`处理文件 ${file.name} 失败:`, error);
      // 继续处理其他文件，而不是中断整个过程
      // 添加原始文件，以便用户仍然可以上传
      processedFiles.push(file);
    }
  }
  return processedFiles;
};

