import { ref, onMounted, computed, reactive } from 'vue';
import { useRouter } from 'vue-router';
import { ElMessage, ElMessageBox, ElImageViewer } from 'element-plus';
import axios from 'axios';
import { Plus, Refresh, User, ArrowDown, ArrowUp, Download, Loading } from '@element-plus/icons-vue';
import auth from '../store/auth';
import { exportToExcelWithImages } from '../utils/exportUtils';
import apiConfig from '../config/api';
import httpService from '../config/httpService';

// API基础URL
// const apiBaseURL = apiConfig.baseURL;

// 解析照片路径
const parsePhotoPath = path => {
  if (!path) return [];
  try {
    // 尝试解析为JSON
    if (path.startsWith('[') && path.endsWith(']')) {
      return JSON.parse(path);
    }
    // 如果不是JSON格式，则将其作为单个路径返回
    return [path];
  } catch (error) {
    console.error('解析照片路径失败:', error);
    // 如果解析失败，将其作为单个路径返回
    return [path];
  }
};

// 获取指定类型的照片
const getPhotosByType = (record, type) => {
  if (!record.photos) return [];
  try {
    const photos = JSON.parse(record.photos);
    if (Array.isArray(photos)) {
      return photos.filter(photo => photo.type === type);
    }
    return [];
  } catch (error) {
    console.error('解析照片JSON失败:', error);
    return [];
  }
};
export default {
  name: 'AdminRecordsView',
  components: {
    Plus,
    Refresh,
    User,
    ArrowDown,
    ArrowUp,
    Download,
    ElImageViewer,
    Loading
  },
  setup() {
    const router = useRouter();
    const records = ref([]);
    const loading = ref(false);
    const units = ref([]);
    const wasteTypes = ref([]);
    const showFilterPanel = ref(true);

    // 添加baseUrl定义
    const baseUrl = apiConfig.baseURL;

    // 图片预览相关
    const showViewer = ref(false);
    const previewImages = ref([]);
    const previewIndex = ref(0);

    // 添加分页相关的响应式变量
    const page = ref(1);
    const pageSize = ref(20);
    const hasMore = ref(true);
    const loadingMore = ref(false);

    // 筛选表单
    const filterForm = reactive({
      unitId: null,
      dateRange: null,
      wasteTypeId: null,
      minQuantity: null,
      maxQuantity: null,
      location: ''
    });

    // 过滤后的记录
    const filteredRecords = computed(() => {
      return records.value.filter(record => {
        // 检查所属单位
        if (filterForm.unitId && record.unit_id !== filterForm.unitId) {
          return false;
        }

        // 检查废物类型
        if (filterForm.wasteTypeId && record.waste_type_id !== filterForm.wasteTypeId) {
          return false;
        }

        // 检查数量范围
        if (filterForm.minQuantity !== null && parseFloat(record.quantity) < filterForm.minQuantity) {
          return false;
        }
        if (filterForm.maxQuantity !== null && parseFloat(record.quantity) > filterForm.maxQuantity) {
          return false;
        }

        // 检查地点关键词
        if (filterForm.location && !record.location.toLowerCase().includes(filterForm.location.toLowerCase())) {
          return false;
        }

        // 检查日期范围
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          const startDate = new Date(filterForm.dateRange[0]);
          // 将结束日期设置为当天的23:59:59，以包含整天
          const endDate = new Date(filterForm.dateRange[1]);
          endDate.setHours(23, 59, 59, 999);

          // 解析记录中的收集开始时间
          if (record.collection_start_time) {
            // 处理已格式化的日期时间字符串
            const recordDate = parseFormattedDateTime(record.collection_start_time);
            if (recordDate < startDate || recordDate > endDate) {
              return false;
            }
          } else {
            // 如果记录没有收集时间，并且筛选设置了日期范围，则排除该记录
            return false;
          }
        }
        return true;
      });
    });

    // 解析已格式化的日期时间字符串
    const parseFormattedDateTime = formattedDateTime => {
      // 假设格式是 "2025-03-01 14:30:00"
      // 移除所有非数字和连字符、冒号的字符
      const cleanedStr = formattedDateTime.replace(/[^0-9\-: ]/g, '');
      return new Date(cleanedStr);
    };
    onMounted(async () => {
      // 验证用户是否为超级管理员
      if (!auth.state.isLoggedIn || auth.state.user.role_id !== 3) {
        ElMessage.error('权限不足');
        router.push('/login');
        return;
      }
      await Promise.all([fetchUnits(), fetchWasteTypes(), fetchRecords()]);
    });

    // 获取单位列表
    const fetchUnits = async () => {
      try {
        const response = await axios.get(apiConfig.getUrl(apiConfig.endpoints.units));
        units.value = response.data;
      } catch (error) {
        console.error('获取单位列表失败:', error);
        ElMessage.error('获取单位列表失败');
      }
    };

    // 获取废物类型列表
    const fetchWasteTypes = async () => {
      try {
        const response = await axios.get(apiConfig.getUrl(apiConfig.endpoints.wasteTypes));
        wasteTypes.value = response.data;
      } catch (error) {
        console.error('获取废物类型列表失败:', error);
        ElMessage.error('获取废物类型列表失败');
      }
    };
    const fetchRecords = async (isLoadMore = false) => {
      if (!isLoadMore) {
        loading.value = true;
        page.value = 1;
        records.value = [];
      } else {
        loadingMore.value = true;
      }
      try {
        const params = {
          page: page.value,
          pageSize: pageSize.value,
          wasteTypeId: filterForm.wasteTypeId,
          minQuantity: filterForm.minQuantity,
          maxQuantity: filterForm.maxQuantity,
          location: filterForm.location
        };

        // Only add dateRange if it exists and has both start and end dates
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          params.dateRange = JSON.stringify(filterForm.dateRange);
        }
        const response = await axios.get(`${apiConfig.getUrl(apiConfig.endpoints.wasteRecords)}/user/${auth.state.user.id}`, {
          params
        });

        // 格式化日期
        const formattedRecords = response.data.records.map(record => ({
          ...record,
          created_at: formatDateTime(record.created_at),
          collection_start_time: formatDateTime(record.collection_start_time)
        }));
        if (isLoadMore) {
          records.value = [...records.value, ...formattedRecords];
        } else {
          records.value = formattedRecords;
        }
        hasMore.value = response.data.hasMore;
        if (hasMore.value) {
          page.value++;
        }
      } catch (error) {
        console.error('获取废物记录失败:', error);
        ElMessage.error('获取废物记录失败');
      } finally {
        loading.value = false;
        loadingMore.value = false;
      }
    };
    const formatDateTime = dateTimeStr => {
      if (!dateTimeStr) return '';
      const date = new Date(dateTimeStr);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });
    };
    const refreshRecords = () => {
      fetchRecords();
    };
    const addNewRecord = () => {
      if (auth.state.user.role_id === 3) {
        router.push('/record/new');
      } else {
        router.push(`/unit/${auth.state.user.unit_id}`);
      }
    };
    const goToUserManagement = () => {
      router.push('/user-management');
    };
    const editRecord = recordId => {
      router.push(`/record/${recordId}`);
    };

    // 应用筛选
    const applyFilter = async () => {
      await fetchRecords();
      ElMessage.success('筛选已应用');
    };

    // 重置筛选
    const resetFilter = async () => {
      filterForm.unitId = null;
      filterForm.dateRange = null;
      filterForm.wasteTypeId = null;
      filterForm.minQuantity = null;
      filterForm.maxQuantity = null;
      filterForm.location = '';
      await fetchRecords();
      ElMessage.info('筛选条件已重置');
    };

    // 导出筛选后的记录为Excel
    const exportRecords = async () => {
      if (filteredRecords.value.length === 0) {
        ElMessage.warning('没有可导出的记录');
        return;
      }
      try {
        loading.value = true;
        console.log('========== 开始导出记录 ==========');
        console.log('使用后端API获取完整数据进行导出');

        // 显示提示信息，告知用户正在处理
        ElMessage.info('正在从服务器获取数据，请稍候...');

        // 获取当前用户ID
        const userId = auth.getUserId();
        if (!userId) {
          throw new Error('用户未登录');
        }

        // 准备查询参数
        const params = {};
        if (filterForm.unitId) params.unitId = filterForm.unitId;
        if (filterForm.wasteTypeId) params.wasteTypeId = filterForm.wasteTypeId;
        if (filterForm.minQuantity !== null) params.minQuantity = filterForm.minQuantity;
        if (filterForm.maxQuantity !== null) params.maxQuantity = filterForm.maxQuantity;
        if (filterForm.location) params.location = filterForm.location;
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          params.dateRange = JSON.stringify(filterForm.dateRange);
        }
        console.log('导出查询参数:', params);

        // 调用后端API获取完整数据
        const response = await httpService.get(`${apiConfig.endpoints.wasteRecordsExportExcel}/${userId}`, {
          params
        });
        if (!response.data || !response.data.success) {
          throw new Error(response.data?.message || '获取导出数据失败');
        }
        const exportData = response.data.data;
        console.log(`从服务器获取到 ${exportData.length} 条记录用于导出`);
        if (exportData.length === 0) {
          ElMessage.warning('没有可导出的记录');
          loading.value = false;
          return;
        }

        // 准备导出数据
        const formattedData = exportData.map(record => {
          // 解析照片路径
          let beforePhotoPaths = [];
          let afterPhotoPaths = [];
          try {
            beforePhotoPaths = JSON.parse(record.photo_path_before || '[]');
            afterPhotoPaths = JSON.parse(record.photo_path_after || '[]');
          } catch (error) {
            console.error('解析照片路径失败:', error);
          }
          return {
            '单位': record.unit_name || '',
            '废物类型': record.waste_type_name || '',
            '收集地点': record.location || '',
            '收集时间': record.collection_start_time || '',
            '数量(kg)': record.quantity || 0,
            '记录时间': record.created_at || '',
            '汇报人': record.creator_name || '未知',
            '清理前照片': '',
            '清理后照片': '',
            '__original': {
              ...record,
              photo_path_before: beforePhotoPaths.length > 0 ? beforePhotoPaths : null,
              photo_path_after: afterPhotoPaths.length > 0 ? afterPhotoPaths : null
            }
          };
        });
        console.log('导出数据准备完成');
        console.log('导出数据示例:', JSON.stringify(formattedData[0]).substring(0, 300) + '...');

        // 导出文件
        const fileName = '危险废物记录';
        const headers = [{
          text: '单位',
          field: '单位'
        }, {
          text: '废物类型',
          field: '废物类型'
        }, {
          text: '收集地点',
          field: '收集地点'
        }, {
          text: '收集时间',
          field: '收集时间'
        }, {
          text: '数量(kg)',
          field: '数量(kg)'
        }, {
          text: '记录时间',
          field: '记录时间'
        }, {
          text: '汇报人',
          field: '汇报人'
        }, {
          text: '清理前照片',
          field: '清理前照片'
        }, {
          text: '清理后照片',
          field: '清理后照片'
        }];

        // 显示提示信息，告知用户正在处理图片
        ElMessage.info('正在处理图片，请稍候...');
        console.log('开始导出Excel文件...');
        console.log('调用exportToExcelWithImages函数');

        // 调用exportToExcelWithImages函数
        const result = await exportToExcelWithImages(formattedData, fileName, headers);
        if (result) {
          ElMessage.success('导出成功');
        } else {
          ElMessage.error('导出失败');
        }
      } catch (error) {
        console.error('导出记录错误:', error);
        ElMessage.error('导出失败: ' + error.message);
      } finally {
        loading.value = false;
      }
    };

    // 确认删除记录
    const confirmDelete = record => {
      ElMessageBox.confirm('此操作将永久删除该记录，是否继续？', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await axios.delete(`${apiConfig.getUrl(apiConfig.endpoints.wasteRecords)}/${record.id}`);
          ElMessage.success('删除成功');
          await fetchRecords();
        } catch (error) {
          console.error('删除记录失败:', error);
          ElMessage.error('删除记录失败');
        }
      }).catch(() => {
        ElMessage.info('已取消删除');
      });
    };

    // 预览照片
    const previewPhoto = (paths, index) => {
      // 确保paths是数组
      const photoArray = Array.isArray(paths) ? paths : [paths];
      previewImages.value = photoArray.map(path => `${baseUrl}${path}`);
      previewIndex.value = index || 0;
      showViewer.value = true;
    };

    // 关闭预览
    const closeViewer = () => {
      showViewer.value = false;
    };

    // 添加滚动加载处理函数
    const handleScroll = async e => {
      const element = e.target;
      // 添加安全检查，确保element存在且有必要的属性
      if (element && element.scrollHeight && !loadingMore.value && hasMore.value && element.scrollHeight - element.scrollTop - element.clientHeight < 100) {
        await fetchRecords(true);
      }
    };
    return {
      records,
      filteredRecords,
      loading,
      units,
      wasteTypes,
      showFilterPanel,
      filterForm,
      applyFilter,
      resetFilter,
      exportRecords,
      parsePhotoPath,
      getPhotosByType,
      refreshRecords,
      addNewRecord,
      goToUserManagement,
      editRecord,
      confirmDelete,
      // 图片预览相关
      showViewer,
      previewImages,
      previewIndex,
      previewPhoto,
      closeViewer,
      baseUrl,
      page,
      pageSize,
      hasMore,
      loadingMore,
      handleScroll
    };
  }
};
