import { ref, onMounted, computed, reactive } from 'vue';
import { useRouter } from 'vue-router';
import { ElMessage, ElMessageBox, ElImageViewer } from 'element-plus';
import axios from 'axios';
import { Plus, Refresh, PictureFailed, User, ArrowDown, ArrowUp, Download, Loading } from '@element-plus/icons-vue';
import auth from '../store/auth';
import { exportToExcel } from '../utils/exportUtils';
import api from '../config/api';

// API基础URL
const apiBaseURL = api.baseURL;

// 解析JSON格式的照片路径
const parsePhotoPath = path => {
  try {
    return JSON.parse(path);
  } catch (error) {
    console.error('解析照片路径失败:', error);
    return [];
  }
};
export default {
  name: 'AdminRecordsView',
  components: {
    Plus,
    Refresh,
    PictureFailed,
    User,
    ArrowDown,
    ArrowUp,
    Download,
    ElImageViewer,
    Loading
  },
  setup() {
    const router = useRouter();
    const records = ref([]);
    const loading = ref(false);
    const units = ref([]);
    const wasteTypes = ref([]);
    const showFilterPanel = ref(true);

    // 图片预览相关
    const showViewer = ref(false);
    const previewImages = ref([]);
    const previewIndex = ref(0);

    // 添加分页相关的响应式变量
    const page = ref(1);
    const pageSize = ref(20);
    const hasMore = ref(true);
    const loadingMore = ref(false);

    // 筛选表单
    const filterForm = reactive({
      unitId: null,
      dateRange: null,
      wasteTypeId: null,
      minQuantity: null,
      maxQuantity: null,
      location: ''
    });

    // 过滤后的记录
    const filteredRecords = computed(() => {
      return records.value.filter(record => {
        // 检查所属单位
        if (filterForm.unitId && record.unit_id !== filterForm.unitId) {
          return false;
        }

        // 检查废物类型
        if (filterForm.wasteTypeId && record.waste_type_id !== filterForm.wasteTypeId) {
          return false;
        }

        // 检查数量范围
        if (filterForm.minQuantity !== null && parseFloat(record.quantity) < filterForm.minQuantity) {
          return false;
        }
        if (filterForm.maxQuantity !== null && parseFloat(record.quantity) > filterForm.maxQuantity) {
          return false;
        }

        // 检查地点关键词
        if (filterForm.location && !record.location.toLowerCase().includes(filterForm.location.toLowerCase())) {
          return false;
        }

        // 检查日期范围
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          const startDate = new Date(filterForm.dateRange[0]);
          // 将结束日期设置为当天的23:59:59，以包含整天
          const endDate = new Date(filterForm.dateRange[1]);
          endDate.setHours(23, 59, 59, 999);

          // 解析记录中的收集开始时间
          if (record.collection_start_time) {
            // 处理已格式化的日期时间字符串
            const recordDate = parseFormattedDateTime(record.collection_start_time);
            if (recordDate < startDate || recordDate > endDate) {
              return false;
            }
          } else {
            // 如果记录没有收集时间，并且筛选设置了日期范围，则排除该记录
            return false;
          }
        }
        return true;
      });
    });

    // 解析已格式化的日期时间字符串
    const parseFormattedDateTime = formattedDateTime => {
      // 假设格式是 "2025-03-01 14:30:00"
      // 移除所有非数字和连字符、冒号的字符
      const cleanedStr = formattedDateTime.replace(/[^0-9\-: ]/g, '');
      return new Date(cleanedStr);
    };
    onMounted(async () => {
      // 验证用户是否为超级管理员
      if (!auth.state.isLoggedIn || auth.state.user.role_id !== 3) {
        ElMessage.error('权限不足');
        router.push('/login');
        return;
      }
      await Promise.all([fetchUnits(), fetchWasteTypes(), fetchRecords()]);
    });

    // 获取单位列表
    const fetchUnits = async () => {
      try {
        const response = await axios.get(api.getUrl(api.endpoints.units));
        units.value = response.data;
      } catch (error) {
        console.error('获取单位列表失败:', error);
        ElMessage.error('获取单位列表失败');
      }
    };

    // 获取废物类型列表
    const fetchWasteTypes = async () => {
      try {
        const response = await axios.get(api.getUrl(api.endpoints.wasteTypes));
        wasteTypes.value = response.data;
      } catch (error) {
        console.error('获取废物类型列表失败:', error);
        ElMessage.error('获取废物类型列表失败');
      }
    };
    const fetchRecords = async (isLoadMore = false) => {
      if (!isLoadMore) {
        loading.value = true;
        page.value = 1;
        records.value = [];
      } else {
        loadingMore.value = true;
      }
      try {
        const params = {
          page: page.value,
          pageSize: pageSize.value,
          wasteTypeId: filterForm.wasteTypeId,
          minQuantity: filterForm.minQuantity,
          maxQuantity: filterForm.maxQuantity,
          location: filterForm.location
        };

        // Only add dateRange if it exists and has both start and end dates
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          params.dateRange = JSON.stringify(filterForm.dateRange);
        }
        const response = await axios.get(`${api.getUrl(api.endpoints.wasteRecords)}/user/${auth.state.user.id}`, {
          params
        });

        // 格式化日期
        const formattedRecords = response.data.records.map(record => ({
          ...record,
          created_at: formatDateTime(record.created_at),
          collection_start_time: formatDateTime(record.collection_start_time)
        }));
        if (isLoadMore) {
          records.value = [...records.value, ...formattedRecords];
        } else {
          records.value = formattedRecords;
        }
        hasMore.value = response.data.hasMore;
        if (hasMore.value) {
          page.value++;
        }
      } catch (error) {
        console.error('获取废物记录失败:', error);
        ElMessage.error('获取废物记录失败');
      } finally {
        loading.value = false;
        loadingMore.value = false;
      }
    };
    const formatDateTime = dateTimeStr => {
      if (!dateTimeStr) return '';
      const date = new Date(dateTimeStr);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });
    };
    const refreshRecords = () => {
      fetchRecords();
    };
    const addNewRecord = () => {
      router.push('/record/new');
    };
    const goToUserManagement = () => {
      router.push('/user-management');
    };
    const editRecord = recordId => {
      router.push(`/record/${recordId}`);
    };

    // 应用筛选
    const applyFilter = async () => {
      await fetchRecords();
      ElMessage.success('筛选已应用');
    };

    // 重置筛选
    const resetFilter = async () => {
      filterForm.unitId = null;
      filterForm.dateRange = null;
      filterForm.wasteTypeId = null;
      filterForm.minQuantity = null;
      filterForm.maxQuantity = null;
      filterForm.location = '';
      await fetchRecords();
      ElMessage.info('筛选条件已重置');
    };

    // 导出筛选后的记录为Excel
    const exportRecords = async () => {
      if (filteredRecords.value.length === 0) {
        ElMessage.warning('没有可导出的记录');
        return;
      }
      try {
        loading.value = true;
        // 获取所有符合当前筛选条件的记录
        const response = await axios.get(`${api.getUrl(api.endpoints.wasteRecords)}/export/user/${auth.state.user.id}`, {
          params: {
            unitId: filterForm.unitId,
            wasteTypeId: filterForm.wasteTypeId,
            minQuantity: filterForm.minQuantity,
            maxQuantity: filterForm.maxQuantity,
            location: filterForm.location,
            dateRange: filterForm.dateRange
          }
        });
        const allRecords = response.data.map(record => ({
          ...record,
          created_at: formatDateTime(record.created_at),
          collection_start_time: formatDateTime(record.collection_start_time)
        }));

        // 定义导出列
        const exportHeaders = [{
          title: '单位',
          field: 'unit_name',
          width: 15
        }, {
          title: '废物类型',
          field: 'waste_type_name',
          width: 15
        }, {
          title: '产生地点',
          field: 'location',
          width: 20
        }, {
          title: '收集开始时间',
          field: 'collection_start_time',
          width: 20,
          type: 'datetime'
        }, {
          title: '数量(吨)',
          field: 'quantity',
          width: 12,
          type: 'number'
        }, {
          title: '记录时间',
          field: 'created_at',
          width: 20,
          type: 'datetime'
        }];
        let fileName = '全部废物记录';
        if (filterForm.unitId) {
          const unit = units.value.find(u => u.id === filterForm.unitId);
          if (unit) {
            fileName = `${unit.name}_废物记录`;
          }
        }
        if (filterForm.wasteTypeId) {
          const wasteType = wasteTypes.value.find(type => type.id === filterForm.wasteTypeId);
          if (wasteType) {
            fileName += `_${wasteType.name}`;
          }
        }
        exportToExcel(allRecords, fileName, exportHeaders);
        ElMessage.success('导出成功');
      } catch (error) {
        console.error('导出记录失败:', error);
        ElMessage.error('导出记录失败');
      } finally {
        loading.value = false;
      }
    };

    // 确认删除记录
    const confirmDelete = record => {
      ElMessageBox.confirm('此操作将永久删除该记录，是否继续？', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await axios.delete(`${api.getUrl(api.endpoints.wasteRecords)}/${record.id}`);
          ElMessage.success('删除成功');
          await fetchRecords();
        } catch (error) {
          console.error('删除记录失败:', error);
          ElMessage.error('删除记录失败');
        }
      }).catch(() => {
        ElMessage.info('已取消删除');
      });
    };

    // 预览照片
    const previewPhoto = (paths, index) => {
      previewImages.value = paths.map(path => `${apiBaseURL}${path}`);
      previewIndex.value = index;
      showViewer.value = true;
    };

    // 关闭预览
    const closeViewer = () => {
      showViewer.value = false;
    };

    // 添加滚动加载处理函数
    const handleScroll = async e => {
      const element = e.target;
      if (!loadingMore.value && hasMore.value && element.scrollHeight - element.scrollTop - element.clientHeight < 100) {
        await fetchRecords(true);
      }
    };
    return {
      records,
      filteredRecords,
      loading,
      units,
      wasteTypes,
      showFilterPanel,
      filterForm,
      applyFilter,
      resetFilter,
      exportRecords,
      parsePhotoPath,
      refreshRecords,
      addNewRecord,
      goToUserManagement,
      editRecord,
      confirmDelete,
      // 图片预览相关
      showViewer,
      previewImages,
      previewIndex,
      previewPhoto,
      closeViewer,
      apiBaseURL,
      page,
      pageSize,
      hasMore,
      loadingMore,
      handleScroll
    };
  }
};
