 * @param {File} file - 原始图片文件
 * @param {number} quality - 压缩质量，范围0-1，默认0.8
 * @param {Function} onProgress - 进度回调函数
 * @returns {Promise<File>} - 处理后的图片文件
 */
export const convertAndCompressImage = (file, quality = 0.8, onProgress) => {
  return new Promise((resolve, reject) => {
    // 创建文件读取器
    const reader = new FileReader();
    
    // 读取文件内容
    reader.onload = (e) => {
      // 创建图片对象
      const img = new Image();
      
      // 图片加载完成后进行处理
      img.onload = () => {
        // 创建canvas
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        
        // 设置canvas尺寸为图片原始尺寸
        canvas.width = img.width;
        canvas.height = img.height;
        
        // 在canvas上绘制图片
        ctx.drawImage(img, 0, 0);
        
        // 模拟压缩过程
        let progress = 0;
        const interval = setInterval(() => {
          progress += 10;
          if (onProgress) {
            onProgress(progress);
          }
          if (progress >= 100) {
            clearInterval(interval);
            // 将canvas内容转换为JPEG格式的Blob
            canvas.toBlob(
              (blob) => {
                // 创建新的File对象
                const newFile = new File([blob], file.name.replace(/\.[^/.]+$/, '.jpg'), {
                  type: 'image/jpeg',
                  lastModified: new Date().getTime()
                });
                resolve(newFile);
              },
              'image/jpeg',
              quality
            );
          }
        }, 100);
      };
      
      // 处理图片加载错误
      img.onerror = () => {
        reject(new Error('图片加载失败'));
      };
      
      // 设置图片源
      img.src = e.target.result;
    };
    
    // 处理文件读取错误
    reader.onerror = () => {
      reject(new Error('文件读取失败'));
    };
    
    // 开始读取文件
    reader.readAsDataURL(file);
  });
};

/**
 * 批量处理图片文件
 * @param {File[]} files - 图片文件数组
 * @param {number} quality - 压缩质量，范围0-1，默认0.8
 * @param {Function} onProgress - 进度回调函数
 * @returns {Promise<File[]>} - 处理后的图片文件数组
 */
export const batchProcessImages = async (files, quality = 0.8, onProgress) => {
  const totalFiles = files.length;
  const processedFiles = [];
  
  for (let i = 0; i < totalFiles; i++) {
    const file = files[i];
    const fileProgress = (progress) => {
      if (onProgress) {
        // 计算总体进度
        const totalProgress = ((i * 100 + progress) / totalFiles);
        onProgress(totalProgress);
      }
    };
    
    try {
      const processedFile = await convertAndCompressImage(file, quality, fileProgress);
      processedFiles.push(processedFile);
    } catch (error) {
      console.error(`处理文件 ${file.name} 失败:`, error);
      throw error;
    }
  }
  
  return processedFiles;
};
