// 检查XLSX库是否正确加载
console.log('XLSX库版本:', XLSX.version);
console.log('XLSX库可用方法:', Object.keys(XLSX).join(', '));

// 解析照片路径函数（与页面中使用的方式相同）
const parsePhotoPath = path => {
  if (!path) return [];
  try {
    // 尝试解析为JSON
    if (typeof path === 'string' && path.startsWith('[') && path.endsWith(']')) {
      return JSON.parse(path);
    }
    // 如果不是JSON格式，则将其作为单个路径返回
    return [path];
  } catch (error) {
    console.error('解析照片路径失败:', error);
    // 如果解析失败，将其作为单个路径返回
    return [path];
  }
};

// 将图片URL转换为Base64
const imageUrlToBase64 = url => {
  console.log('开始转换图片URL到Base64:', url);

  // 检查URL是否有效
  if (!url || typeof url !== 'string') {
    console.error('无效的图片URL:', url);
    return Promise.reject(new Error('Invalid image URL'));
  }

  // 检查URL是否为相对路径，如果是，添加origin
  let fullUrl = url;
  if (url.startsWith('/')) {
    fullUrl = window.location.origin + url;
    console.log('转换为完整URL:', fullUrl);
  }
  return new Promise((resolve, reject) => {
    // 尝试使用fetch API先获取图片，可以更好地处理跨域问题
    console.log('使用fetch API获取图片:', fullUrl);
    fetch(fullUrl, {
      mode: 'cors',
      credentials: 'same-origin'
    }).then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      console.log('fetch成功，状态:', response.status);
      return response.blob();
    }).then(blob => {
      console.log('获取到blob数据，大小:', blob.size, '类型:', blob.type);

      // 使用FileReader将blob转换为base64
      const reader = new FileReader();
      reader.onload = e => {
        console.log('FileReader加载成功，数据长度:', e.target.result.length);
        resolve(e.target.result);
      };
      reader.onerror = error => {
        console.error('FileReader错误:', error);
        reject(error);
      };
      reader.readAsDataURL(blob);
    }).catch(error => {
      console.error('fetch失败，回退到Image方法:', error);

      // 如果fetch失败，回退到Image方法
      const img = new Image();
      img.crossOrigin = 'Anonymous'; // 处理跨域问题
      console.log('设置图片crossOrigin为Anonymous');
      img.onload = () => {
        console.log('图片加载成功，尺寸:', img.width, 'x', img.height);
        try {
          const canvas = document.createElement('canvas');
          canvas.width = img.width;
          canvas.height = img.height;
          console.log('创建canvas元素，尺寸:', canvas.width, 'x', canvas.height);
          const ctx = canvas.getContext('2d');
          ctx.drawImage(img, 0, 0);
          console.log('图片已绘制到canvas');
          const dataURL = canvas.toDataURL('image/jpeg');
          console.log('转换为dataURL成功，长度:', dataURL.length);
          console.log('dataURL前30个字符:', dataURL.substring(0, 30) + '...');
          resolve(dataURL);
        } catch (error) {
          console.error('转换图片失败:', error);
          console.error('错误详情:', error.message);
          console.error('错误堆栈:', error.stack);
          reject(error);
        }
      };
      img.onerror = error => {
        console.error('加载图片失败:', error);
        console.error('图片URL:', fullUrl);
        console.error('错误类型:', typeof error);
        console.error('错误详情:', error ? error.message : 'Unknown error');
        reject(error);
      };
      console.log('设置图片src:', fullUrl);
      img.src = fullUrl;

      // 添加超时处理
      setTimeout(() => {
        if (!img.complete) {
          console.error('图片加载超时:', fullUrl);
          reject(new Error('Image loading timeout'));
        }
      }, 10000); // 10秒超时
    });
  });
};

