      throw exportError;
    }

    // 导出完成后移除临时表格
    document.body.removeChild(table);
    console.log('临时表格已移除');
    return true;
  } catch (error) {
    console.error('使用js-table2excel导出Excel失败:', error);
    console.error('错误详情:', error.message);
    console.error('错误堆栈:', error.stack);

    // 如果js-table2excel导出失败，尝试使用CSV导出
    try {
      console.log('尝试使用CSV导出...');

      // 准备CSV内容
      let csvContent = '';

      // 添加表头
      csvContent += headers.map(header => `"${header.text}"`).join(',') + '\n';

      // 添加数据行
      data.forEach(row => {
        const rowData = headers.map(header => {
          const value = row[header.field] || '';
