import { ref, onMounted, computed, reactive } from 'vue';
import { useRouter } from 'vue-router';
import { ElMessage, ElMessageBox, ElImageViewer } from 'element-plus';
import httpService from '../config/httpService';
import apiConfig from '../config/api';
import { ArrowLeft, Home, Refresh, PictureFailed, Plus, User, ArrowDown, ArrowUp, Download, Loading } from '@element-plus/icons-vue';
import { exportToExcel } from '../utils/exportUtils';
import auth from '../store/auth';

// 解析JSON格式的照片路径
const parsePhotoPath = path => {
  try {
    return JSON.parse(path);
  } catch (error) {
    console.error('解析照片路径失败:', error);
    return [];
  }
};
export default {
  name: 'RecordsList',
  components: {
    ArrowLeft,
    Home,
    Refresh,
    PictureFailed,
    Plus,
    User,
    ArrowDown,
    ArrowUp,
    Download,
    ElImageViewer,
    Loading
  },
  props: {
    unitId: {
      type: [String, Number],
      required: true
    }
  },
  setup(props) {
    const router = useRouter();
    const records = ref([]);
    const loading = ref(false);
    const unitName = ref('');
    const wasteTypes = ref([]);
    const showFilterPanel = ref(true);

    // 添加分页相关的响应式变量
    const page = ref(1);
    const pageSize = ref(20);
    const hasMore = ref(true);
    const loadingMore = ref(false);

    // 筛选表单
    const filterForm = reactive({
      dateRange: null,
      wasteTypeId: null,
      minQuantity: null,
      maxQuantity: null,
      location: ''
    });

    // 过滤后的记录
    const filteredRecords = computed(() => {
      return records.value.filter(record => {
        // 检查废物类型
        if (filterForm.wasteTypeId && record.waste_type_id !== filterForm.wasteTypeId) {
          return false;
        }

        // 检查数量范围
        if (filterForm.minQuantity !== null && parseFloat(record.quantity) < filterForm.minQuantity) {
          return false;
        }
        if (filterForm.maxQuantity !== null && parseFloat(record.quantity) > filterForm.maxQuantity) {
          return false;
        }

        // 检查地点关键词
        if (filterForm.location && !record.location.toLowerCase().includes(filterForm.location.toLowerCase())) {
          return false;
        }

        // 检查日期范围
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          const startDate = new Date(filterForm.dateRange[0]);
          // 将结束日期设置为当天的23:59:59，以包含整天
          const endDate = new Date(filterForm.dateRange[1]);
          endDate.setHours(23, 59, 59, 999);

          // 解析记录中的收集开始时间
          if (record.collection_start_time) {
            // 处理已格式化的日期时间字符串
            const recordDate = parseFormattedDateTime(record.collection_start_time);
            if (recordDate < startDate || recordDate > endDate) {
              return false;
            }
          } else {
            // 如果记录没有收集时间，并且筛选设置了日期范围，则排除该记录
            return false;
          }
        }
        return true;
      });
    });

    // 解析已格式化的日期时间字符串
    const parseFormattedDateTime = formattedDateTime => {
      // 假设格式是 "2025-03-01 14:30:00"
      // 移除所有非数字和连字符、冒号的字符
      const cleanedStr = formattedDateTime.replace(/[^0-9\-: ]/g, '');
      return new Date(cleanedStr);
    };

    // 检查用户是否为超级管理员
    const isAdmin = computed(() => {
      return auth.state.isLoggedIn && auth.state.user.role_id === 3;
    });

    // 检查用户是否为单位管理员
    const isUnitAdmin = computed(() => {
      return auth.state.isLoggedIn && auth.state.user.role_id === 2;
    });

    // 图片预览相关
    const showViewer = ref(false);
    const previewImages = ref([]);
    const previewIndex = ref(0);

    // 预览照片
    const previewPhoto = (paths, index) => {
      previewImages.value = paths.map(path => `${apiConfig.baseURL}${path}`);
      previewIndex.value = index;
      showViewer.value = true;
    };

    // 关闭预览
    const closeViewer = () => {
      showViewer.value = false;
    };
    onMounted(async () => {
      await fetchUnitName();
      await fetchWasteTypes();
      await fetchRecords();
    });

    // 获取废物类型列表
    const fetchWasteTypes = async () => {
      try {
        const response = await httpService.get(apiConfig.endpoints.wasteTypes);
        wasteTypes.value = response.data;
      } catch (error) {
        console.error('Error fetching waste types:', error);
        ElMessage.error('获取废物类型列表失败');
      }
    };
    const fetchUnitName = async () => {
      try {
        const response = await httpService.get(apiConfig.endpoints.units);
        const unit = response.data.find(u => u.id === parseInt(props.unitId));
        if (unit) {
          unitName.value = unit.name;
        }
      } catch (error) {
        console.error('Error fetching unit name:', error);
        ElMessage.error('获取单位信息失败');
      }
    };
    const fetchRecords = async (isLoadMore = false) => {
      if (!isLoadMore) {
        loading.value = true;
        page.value = 1;
        records.value = [];
      } else {
        loadingMore.value = true;
      }
      try {
        const params = {
          page: page.value,
          pageSize: pageSize.value
        };
        const response = await httpService.get(`${apiConfig.endpoints.wasteRecords}/user/${auth.state.user.id}`, {
          params
        });

        // 格式化日期
        const formattedRecords = response.data.records.map(record => ({
          ...record,
          created_at: formatDateTime(record.created_at),
          collection_start_time: formatDateTime(record.collection_start_time)
        }));
        if (isLoadMore) {
          records.value = [...records.value, ...formattedRecords];
        } else {
          records.value = formattedRecords;
        }
        hasMore.value = response.data.hasMore;
        if (hasMore.value) {
          page.value++;
        }
      } catch (error) {
        console.error('获取废物记录失败:', error);
        ElMessage.error('获取废物记录失败');
      } finally {
        loading.value = false;
        loadingMore.value = false;
      }
    };
    const formatDateTime = dateTimeStr => {
      if (!dateTimeStr) return '';
      const date = new Date(dateTimeStr);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      });
    };
    const refreshRecords = () => {
      fetchRecords();
    };
    const goBack = () => {
      router.push({
        name: 'WasteForm',
        params: {
          id: props.unitId
        }
      });
    };

    // 返回首页
    const goHome = () => {
      // 只有超级管理员才能回到单位选择页面
      if (isAdmin.value) {
        router.push('/');
      }
    };

    // 前往人员管理页面
    const goToUserManagement = () => {
      router.push('/user-management');
    };

    // 添加新的废物记录
    const addNewRecord = () => {
      if (isAdmin.value) {
        router.push('/record/new');
      } else {
        router.push(`/unit/${auth.state.user.unit_id}`);
      }
    };

    // 编辑现有记录
    const editRecord = recordId => {
      router.push(`/record/${recordId}`);
    };

    // 检查是否可以编辑记录
    // 超级管理员可以编辑所有记录
    // 单位管理员可以编辑本单位所有记录
    // 普通员工只能编辑自己创建的记录
    const canEdit = record => {
      if (!auth.state.isLoggedIn) return false;
      if (auth.state.user.role_id === 3) return true; // 超级管理员

      if (auth.state.user.role_id === 2 && auth.state.user.unit_id === record.unit_id) return true; // 单位管理员

      // 员工只能编辑自己创建的记录
      // 注意: creator_id 可能为 null （旧记录），如果为 null 我们允许编辑
      return auth.state.user.unit_id === record.unit_id && (record.creator_id === null || record.creator_id === auth.state.user.id);
    };

    // 添加滚动加载处理函数
    const handleScroll = async e => {
      const element = e.target;
      if (!loadingMore.value && hasMore.value && element.scrollHeight - element.scrollTop - element.clientHeight < 100) {
        await fetchRecords(true);
      }
    };

    // 添加应用筛选方法
    const applyFilter = async () => {
      await fetchRecords();
      ElMessage.success('筛选已应用');
    };

    // 添加重置筛选方法
    const resetFilter = async () => {
      filterForm.dateRange = null;
      filterForm.wasteTypeId = null;
      filterForm.minQuantity = null;
      filterForm.maxQuantity = null;
      filterForm.location = '';
      await fetchRecords();
      ElMessage.info('筛选条件已重置');
    };

    // 修改导出记录功能，获取所有符合筛选条件的记录
    const exportRecords = async () => {
      if (filteredRecords.value.length === 0) {
        ElMessage.warning('没有可导出的记录');
        return;
      }
      try {
        loading.value = true;
        // 获取所有符合当前筛选条件的记录
        const response = await httpService.get(`${apiConfig.endpoints.wasteRecords}/export/user/${auth.state.user.id}`, {
          params: {
            wasteTypeId: filterForm.wasteTypeId,
            minQuantity: filterForm.minQuantity,
            maxQuantity: filterForm.maxQuantity,
            location: filterForm.location,
            dateRange: filterForm.dateRange
          }
        });
        const allRecords = response.data.map(record => ({
          ...record,
          created_at: formatDateTime(record.created_at),
          collection_start_time: formatDateTime(record.collection_start_time)
        }));

        // 定义导出列
        const exportHeaders = [{
          title: '单位',
          field: 'unit_name',
          width: 15
        }, {
          title: '废物类型',
          field: 'waste_type_name',
          width: 15
        }, {
          title: '产生地点',
          field: 'location',
          width: 20
        }, {
          title: '收集开始时间',
          field: 'collection_start_time',
          width: 20,
          type: 'datetime'
        }, {
          title: '数量(吨)',
          field: 'quantity',
          width: 12,
          type: 'number'
        }, {
          title: '记录时间',
          field: 'created_at',
          width: 20,
          type: 'datetime'
        }];
        let fileName = `${unitName.value}_废物记录`;
        if (filterForm.wasteTypeId) {
          const wasteType = wasteTypes.value.find(type => type.id === filterForm.wasteTypeId);
          if (wasteType) {
            fileName += `_${wasteType.name}`;
          }
        }
        exportToExcel(allRecords, fileName, exportHeaders);
        ElMessage.success('导出成功');
      } catch (error) {
        console.error('导出记录失败:', error);
        ElMessage.error('导出记录失败');
      } finally {
        loading.value = false;
      }
    };
    const confirmDelete = record => {
      ElMessageBox.confirm('此操作将永久删除该记录，是否继续？', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await httpService.delete(`${apiConfig.endpoints.wasteRecords}/${record.id}`);
          ElMessage.success('删除成功');
          await fetchRecords();
        } catch (error) {
          console.error('删除记录失败:', error);
          ElMessage.error('删除记录失败');
        }
      }).catch(() => {
        ElMessage.info('已取消删除');
      });
    };
    return {
      records,
      filteredRecords,
      loading,
      unitName,
      wasteTypes,
      isAdmin,
      isUnitAdmin,
      parsePhotoPath,
      refreshRecords,
      goBack,
      goHome,
      goToUserManagement,
      addNewRecord,
      editRecord,
      canEdit,
      confirmDelete,
      apiConfig,
      // 筛选相关
      showFilterPanel,
      filterForm,
      applyFilter,
      resetFilter,
      // 导出相关
      exportRecords,
      // 图片预览相关
      showViewer,
      previewImages,
      previewIndex,
      previewPhoto,
      closeViewer,
      // 分页相关
      page,
      pageSize,
      hasMore,
      loadingMore,
      handleScroll
    };
  }
};
