// 用于导出数据到CSV和Excel的工具函数
import * as XLSX from 'xlsx';
import Table2Excel from 'js-table2excel';
// import { saveAs } from 'file-saver';

// 检查XLSX库是否正确加载
console.log('XLSX库版本:', XLSX.version);
console.log('XLSX库可用方法:', Object.keys(XLSX).join(', '));

// 解析照片路径
const parsePhotoPath = path => {
  console.log('【照片处理】开始解析照片路径:', path);
  console.log('【照片处理】照片路径类型:', typeof path);
  if (!path) {
    console.log('【照片处理】照片路径为空，返回空数组');
    return [];
  }
  try {
    // 尝试解析为JSON
    if (typeof path === 'string' && path.startsWith('[') && path.endsWith(']')) {
      console.log('【照片处理】照片路径是JSON格式，尝试解析');
      const parsed = JSON.parse(path);
      console.log('【照片处理】JSON解析结果:', parsed);
      console.log('【照片处理】解析后的照片路径数量:', parsed.length);
      return parsed;
    }

    // 如果是数组，直接返回
    if (Array.isArray(path)) {
      console.log('【照片处理】照片路径已经是数组格式:', path);
      console.log('【照片处理】照片路径数量:', path.length);
      return path;
    }

    // 如果不是JSON格式，则将其作为单个路径返回
    console.log('【照片处理】照片路径不是JSON格式，作为单个路径返回:', [path]);
    return [path];
  } catch (error) {
    console.error('【照片处理】解析照片路径失败:', error);
    console.error('【照片处理】错误详情:', error.message);
    console.error('【照片处理】错误堆栈:', error.stack);
    // 如果解析失败，将其作为单个路径返回
    console.log('【照片处理】解析失败，作为单个路径返回:', [path]);
    return [path];
  }
};

// 将图片URL转换为Base64
const imageUrlToBase64 = url => {
  return new Promise((resolve, reject) => {
    console.log('【照片处理】开始将图片URL转换为Base64');
    console.log('【照片处理】原始URL:', url);

    // 验证URL
    if (!url) {
      const error = new Error('图片URL为空');
      console.error('【照片处理】图片URL为空，转换失败');
      reject(error);
      return;
    }

    // 添加时间戳避免缓存
    const timestamp = new Date().getTime();
    let imageUrl = url;

    // 如果URL包含?，则添加&timestamp=，否则添加?timestamp=
    if (url.includes('?')) {
      imageUrl = `${url}&_t=${timestamp}`;
    } else {
      imageUrl = `${url}?_t=${timestamp}`;
    }
    console.log('【照片处理】添加时间戳后的URL:', imageUrl);

    // 使用fetch API获取图片
    console.log('【照片处理】开始使用fetch获取图片');
    fetch(imageUrl, {
      method: 'GET',
      mode: 'cors',
      cache: 'no-cache',
      credentials: 'same-origin',
      headers: {
        'Cache-Control': 'no-cache',
        'Pragma': 'no-cache'
      }
    }).then(response => {
      console.log('【照片处理】fetch响应状态:', response.status);
      console.log('【照片处理】fetch响应头:', JSON.stringify([...response.headers.entries()]));
      if (!response.ok) {
        throw new Error(`HTTP错误，状态码: ${response.status}`);
      }
      console.log('【照片处理】fetch响应成功，开始获取blob');
      return response.blob();
    }).then(blob => {
      console.log('【照片处理】获取到图片blob数据');
      console.log('【照片处理】blob类型:', blob.type);
      console.log('【照片处理】blob大小:', blob.size, 'bytes');

      // 使用FileReader将blob转换为Base64
      const reader = new FileReader();
      console.log('【照片处理】创建FileReader对象，准备读取blob');
      reader.onload = () => {
        const base64data = reader.result;
        console.log('【照片处理】FileReader读取完成，获取到Base64数据');
        console.log('【照片处理】Base64数据长度:', base64data.length);
        console.log('【照片处理】Base64数据前100个字符:', base64data.substring(0, 100) + '...');
        resolve(base64data);
      };
      reader.onerror = error => {
        console.error('【照片处理】FileReader读取失败:', error);
        console.error('【照片处理】错误详情:', error.message);
        reject(new Error('FileReader读取失败'));
      };
      console.log('【照片处理】开始使用FileReader读取blob');
      reader.readAsDataURL(blob);
    }).catch(error => {
      console.error('【照片处理】fetch或处理过程中出错:', error);
      console.error('【照片处理】错误详情:', error.message);
      console.error('【照片处理】错误堆栈:', error.stack);

      // 尝试使用Image对象作为备选方案
      console.log('【照片处理】尝试使用Image对象作为备选方案');
      const img = new Image();
      img.crossOrigin = 'Anonymous';
      img.onload = () => {
        console.log('【照片处理】Image对象加载成功');
        console.log('【照片处理】图片尺寸:', img.width, 'x', img.height);
        try {
          // 创建canvas并绘制图片
          const canvas = document.createElement('canvas');
          canvas.width = img.width;
          canvas.height = img.height;
          console.log('【照片处理】创建Canvas元素，尺寸:', canvas.width, 'x', canvas.height);
          const ctx = canvas.getContext('2d');
          ctx.drawImage(img, 0, 0);
          console.log('【照片处理】图片已绘制到Canvas');

          // 将canvas内容转换为Base64
          const dataURL = canvas.toDataURL('image/jpeg');
          console.log('【照片处理】Canvas转换为Base64数据成功');
          console.log('【照片处理】Base64数据长度:', dataURL.length);
          console.log('【照片处理】Base64数据前100个字符:', dataURL.substring(0, 100) + '...');
          resolve(dataURL);
        } catch (canvasError) {
          console.error('【照片处理】Canvas处理失败:', canvasError);
          console.error('【照片处理】错误详情:', canvasError.message);
          console.error('【照片处理】错误堆栈:', canvasError.stack);
          reject(canvasError);
        }
      };
      img.onerror = imgError => {
        console.error('【照片处理】Image对象加载失败:', imgError);
        console.error('【照片处理】错误详情:', imgError.message);
        console.error('【照片处理】所有尝试均失败，无法加载图片:', url);
        reject(new Error('图片加载失败'));
      };
      console.log('【照片处理】设置Image对象的src:', imageUrl);
      img.src = imageUrl;
    });
  });
};

/**
 * 使用js-table2excel将数据导出为Excel文件，包含图片
 * @param {Array} data - 要导出的数据数组
 * @param {String} fileName - 导出的文件名（不含后缀）
 * @param {Array} headers - 要导出的列标题和对应字段名
 */
export const exportToExcelWithImages = async (data, fileName, headers) => {
  console.log('=== 【调试】exportToExcelWithImages 函数被调用 ===');
  console.log('【调试】数据条数:', data.length);
  console.log('【调试】headers:', JSON.stringify(headers));
  if (!data || data.length === 0) {
    console.error('【调试】导出失败：没有数据');
    return false;
  }
  try {
    console.log('【调试】使用js-table2excel导出Excel，开始处理图片...');
    console.log('【调试】第一条数据示例:', JSON.stringify(data[0]).substring(0, 200) + '...');

    // 处理文件名
    fileName = fileName.replace(/[\\/:*?"<>|]/g, '_');
    const timestamp = new Date().toISOString().replace(/[-:.]/g, '').substring(0, 14);
    const fullFileName = `${fileName}_${timestamp}`;
    console.log('【调试】导出文件名:', fullFileName);

    // 预处理图片，确保所有图片都加载完成
    const processedData = [];

    // 逐行处理数据
    for (let rowIndex = 0; rowIndex < data.length; rowIndex++) {
      console.log(`【调试】处理第 ${rowIndex + 1}/${data.length} 条记录...`);
      const row = data[rowIndex];
      const record = row['__original'] || {};
      console.log(`【调试】记录ID: ${record.id || 'unknown'}`);
      console.log(`【调试】记录数据:`, JSON.stringify(record).substring(0, 200) + '...');

      // 处理清理前照片
      let beforePhotoData = null;
      if (record.photo_path_before) {
        console.log(`【调试】记录 ${rowIndex} 清理前照片路径:`, record.photo_path_before);
        console.log(`【调试】照片路径类型:`, typeof record.photo_path_before);
        const photoPaths = parsePhotoPath(record.photo_path_before);
        console.log(`【调试】解析后的照片路径:`, photoPaths);
        console.log(`【调试】解析后的照片路径长度:`, photoPaths.length);
        if (photoPaths && photoPaths.length > 0) {
          const firstPath = photoPaths[0];
          console.log(`【调试】选择第一张照片:`, firstPath);
          console.log(`【调试】照片路径类型:`, typeof firstPath);

          // 检查路径格式
          if (typeof firstPath === 'string') {
            // 构建完整URL
            let imageUrl = firstPath;

            // 如果是相对路径，添加origin
            if (firstPath.startsWith('/')) {
              imageUrl = window.location.origin + firstPath;
              console.log(`【调试】照片是相对路径，添加origin:`, window.location.origin);
            }
            console.log(`【调试】记录 ${rowIndex} 清理前照片完整URL:`, imageUrl);
            try {
              console.log(`【调试】开始转换清理前照片...`);
              beforePhotoData = await imageUrlToBase64(imageUrl);
              console.log(`【调试】记录 ${rowIndex} 清理前照片已转换为Base64，长度:`, beforePhotoData ? beforePhotoData.length : 0);
              console.log(`【调试】Base64数据前50个字符:`, beforePhotoData ? beforePhotoData.substring(0, 50) + '...' : 'null');
            } catch (error) {
              console.error(`【调试】记录 ${rowIndex} 清理前照片转换失败:`, error);
              console.error('【调试】错误详情:', error.message);
              console.error('【调试】错误堆栈:', error.stack);
            }
          } else {
            console.error(`【调试】记录 ${rowIndex} 清理前照片路径格式错误:`, firstPath);
          }
        } else {
          console.log(`【调试】记录 ${rowIndex} 没有有效的清理前照片路径`);
        }
      } else {
        console.log(`【调试】记录 ${rowIndex} 没有清理前照片`);
      }

      // 处理清理后照片
      let afterPhotoData = null;
      if (record.photo_path_after) {
        console.log(`【调试】记录 ${rowIndex} 清理后照片路径:`, record.photo_path_after);
        console.log(`【调试】照片路径类型:`, typeof record.photo_path_after);
        const photoPaths = parsePhotoPath(record.photo_path_after);
        console.log(`【调试】解析后的照片路径:`, photoPaths);
        console.log(`【调试】解析后的照片路径长度:`, photoPaths.length);
        if (photoPaths && photoPaths.length > 0) {
          const firstPath = photoPaths[0];
          console.log(`【调试】选择第一张照片:`, firstPath);
          console.log(`【调试】照片路径类型:`, typeof firstPath);

          // 检查路径格式
          if (typeof firstPath === 'string') {
            // 构建完整URL
            let imageUrl = firstPath;

            // 如果是相对路径，添加origin
            if (firstPath.startsWith('/')) {
              imageUrl = window.location.origin + firstPath;
              console.log(`【调试】照片是相对路径，添加origin:`, window.location.origin);
            }
            console.log(`【调试】记录 ${rowIndex} 清理后照片完整URL:`, imageUrl);
            try {
              console.log(`【调试】开始转换清理后照片...`);
              afterPhotoData = await imageUrlToBase64(imageUrl);
              console.log(`【调试】记录 ${rowIndex} 清理后照片已转换为Base64，长度:`, afterPhotoData ? afterPhotoData.length : 0);
              console.log(`【调试】Base64数据前50个字符:`, afterPhotoData ? afterPhotoData.substring(0, 50) + '...' : 'null');
            } catch (error) {
              console.error(`【调试】记录 ${rowIndex} 清理后照片转换失败:`, error);
              console.error('【调试】错误详情:', error.message);
              console.error('【调试】错误堆栈:', error.stack);
            }
          } else {
            console.error(`【调试】记录 ${rowIndex} 清理后照片路径格式错误:`, firstPath);
          }
        } else {
          console.log(`【调试】记录 ${rowIndex} 没有有效的清理后照片路径`);
        }
      } else {
        console.log(`【调试】记录 ${rowIndex} 没有清理后照片`);
      }

      // 保存处理后的数据
      processedData.push({
        ...row,
        __beforePhotoData: beforePhotoData,
        __afterPhotoData: afterPhotoData
      });
      console.log(`【调试】记录 ${rowIndex} 处理完成，是否有照片数据:`, {
        beforePhoto: !!beforePhotoData,
        afterPhoto: !!afterPhotoData
      });
    }
    console.log('【调试】所有图片处理完成，开始创建Excel...');
    console.log('【调试】处理后的数据条数:', processedData.length);

    // 检查是否有任何图片数据
    let hasImageData = false;
    for (const row of processedData) {
      if (row.__beforePhotoData || row.__afterPhotoData) {
        hasImageData = true;
        console.log('【调试】找到图片数据，将使用图片导出模式');
        break;
      }
    }
    if (!hasImageData) {
      console.warn('【调试】没有找到任何有效的图片数据，但仍将尝试使用图片导出模式');
    }

    // 创建一个临时表格元素
    const table = document.createElement('table');
    table.style.display = 'none';
    document.body.appendChild(table);
    console.log('【调试】创建临时表格元素');

    // 创建表头
    const thead = document.createElement('thead');
    const headerRow = document.createElement('tr');
    headers.forEach(header => {
      const th = document.createElement('th');
      th.textContent = header.text;
      headerRow.appendChild(th);
      console.log(`【调试】添加表头: ${header.text}`);
    });
    thead.appendChild(headerRow);
    table.appendChild(thead);
    console.log('【调试】表头创建完成');

    // 创建表体
    const tbody = document.createElement('tbody');

    // 添加数据行
    processedData.forEach((row, index) => {
      console.log(`【调试】创建第 ${index + 1}/${processedData.length} 行...`);
      const tr = document.createElement('tr');
      headers.forEach(header => {
        const td = document.createElement('td');

        // 处理清理前照片列
        if (header.field === '清理前照片') {
          if (row.__beforePhotoData) {
            console.log(`【调试】添加第 ${index + 1} 行清理前照片，Base64长度:`, row.__beforePhotoData.length);
            // 使用Base64数据创建图片
            const img = document.createElement('img');
            img.src = row.__beforePhotoData;
            img.style.width = '100px';
            img.style.height = '100px';
            img.alt = '清理前照片';
            console.log(`【调试】创建图片元素，设置src长度:`, row.__beforePhotoData.length);

            // 确保图片已加载
            if (!img.complete) {
              console.log('【调试】图片尚未加载完成，添加onload事件');
              img.onload = () => {
                console.log(`【调试】第 ${index + 1} 行清理前照片加载完成`);
              };
              img.onerror = error => {
                console.error(`【调试】第 ${index + 1} 行清理前照片加载失败:`, error);
                td.textContent = '图片加载失败';
              };
            } else {
              console.log(`【调试】第 ${index + 1} 行清理前照片已加载完成`);
            }
            td.appendChild(img);
            console.log(`【调试】第 ${index + 1} 行清理前照片添加成功`);
          } else {
            td.textContent = '无图片';
            console.log(`【调试】第 ${index + 1} 行没有清理前照片`);
          }
        }
        // 处理清理后照片列 
        else if (header.field === '清理后照片') {
          if (row.__afterPhotoData) {
            console.log(`【调试】添加第 ${index + 1} 行清理后照片，Base64长度:`, row.__afterPhotoData.length);
            // 使用Base64数据创建图片
            const img = document.createElement('img');
            img.src = row.__afterPhotoData;
            img.style.width = '100px';
            img.style.height = '100px';
            img.alt = '清理后照片';
            console.log(`【调试】创建图片元素，设置src长度:`, row.__afterPhotoData.length);

            // 确保图片已加载
            if (!img.complete) {
              console.log('【调试】图片尚未加载完成，添加onload事件');
              img.onload = () => {
                console.log(`【调试】第 ${index + 1} 行清理后照片加载完成`);
              };
              img.onerror = error => {
                console.error(`【调试】第 ${index + 1} 行清理后照片加载失败:`, error);
                td.textContent = '图片加载失败';
              };
            } else {
              console.log(`【调试】第 ${index + 1} 行清理后照片已加载完成`);
            }
            td.appendChild(img);
            console.log(`【调试】第 ${index + 1} 行清理后照片添加成功`);
          } else {
            td.textContent = '无图片';
            console.log(`【调试】第 ${index + 1} 行没有清理后照片`);
          }
        } else {
          // 普通文本列
          td.textContent = row[header.field] || '';
          console.log(`【调试】添加第 ${index + 1} 行 ${header.field} 列: ${row[header.field] || '(空)'}`);
        }
        tr.appendChild(td);
      });
      tbody.appendChild(tr);
      console.log(`【调试】第 ${index + 1} 行创建完成`);
    });
    table.appendChild(tbody);
    console.log('【调试】表格创建完成');

    // 检查表格内容
    console.log('【调试】表格行数:', table.rows.length);
    console.log('【调试】表格列数:', table.rows[0] ? table.rows[0].cells.length : 0);

    // 使用js-table2excel导出
    console.log('【调试】开始使用js-table2excel导出...');
    const table2excel = new Table2Excel();
    console.log('【调试】Table2Excel实例创建成功');

    // 确保所有图片都已加载完成
    console.log('【调试】检查表格中的图片...');
    const imgElements = table.querySelectorAll('img');
    console.log(`【调试】表格中共有 ${imgElements.length} 张图片`);

    // 检查每个图片的状态
    Array.from(imgElements).forEach((img, idx) => {
      console.log(`【调试】图片 ${idx + 1} 状态:`, {
        complete: img.complete,
        naturalWidth: img.naturalWidth,
        naturalHeight: img.naturalHeight,
        src长度: img.src.length
      });
    });

    // 如果没有图片，直接导出
    if (imgElements.length === 0) {
      console.log('【调试】表格中没有图片，直接导出');
      try {
        console.log('【调试】调用export方法...');
        table2excel.export(table, fullFileName);
        console.log('【调试】export方法调用成功');

        // 导出完成后移除临时表格
        document.body.removeChild(table);
        console.log('【调试】临时表格已移除');
        return true;
      } catch (exportError) {
        console.error('【调试】export方法调用失败:', exportError);
        console.error('【调试】错误详情:', exportError.message);
        console.error('【调试】错误堆栈:', exportError.stack);

        // 导出完成后移除临时表格
        document.body.removeChild(table);
        console.log('【调试】临时表格已移除');
        throw exportError;
      }
    } else {
      // 等待所有图片加载完成
      console.log('【调试】等待所有图片加载完成后再导出');

      // 创建一个Promise数组，每个Promise对应一个图片的加载
      const imgPromises = Array.from(imgElements).map((img, imgIndex) => {
        return new Promise(resolve => {
          if (img.complete) {
            console.log(`【调试】图片 ${imgIndex + 1} 已加载完成，尺寸:`, img.naturalWidth, 'x', img.naturalHeight);
            resolve();
          } else {
            console.log(`【调试】等待图片 ${imgIndex + 1} 加载...`);
            img.onload = () => {
              console.log(`【调试】图片 ${imgIndex + 1} 加载完成，尺寸:`, img.naturalWidth, 'x', img.naturalHeight);
              resolve();
            };
            img.onerror = error => {
              console.error(`【调试】图片 ${imgIndex + 1} 加载失败:`, error);
              console.error('【调试】错误详情:', error ? error.message : 'Unknown error');
              resolve(); // 即使失败也继续导出
            };

            // 添加超时处理
            setTimeout(() => {
              if (!img.complete) {
                console.error(`【调试】图片 ${imgIndex + 1} 加载超时`);
                resolve(); // 超时也继续导出
              }
            }, 5000); // 5秒超时
          }
        });
      });

      // 等待所有图片加载完成或超时
      try {
        await Promise.all(imgPromises);
        console.log('【调试】所有图片加载完成或超时，开始导出');

        // 再次检查图片状态
        console.log('【调试】导出前最终检查图片状态:');
        Array.from(imgElements).forEach((img, idx) => {
          console.log(`【调试】图片 ${idx + 1} 最终状态:`, {
            complete: img.complete,
            naturalWidth: img.naturalWidth,
            naturalHeight: img.naturalHeight,
            src长度: img.src.length
          });
        });
        try {
          console.log('【调试】调用export方法...');
          table2excel.export(table, fullFileName);
          console.log('【调试】export方法调用成功');

          // 导出完成后移除临时表格
          document.body.removeChild(table);
          console.log('【调试】临时表格已移除');
          return true;
        } catch (exportError) {
          console.error('【调试】export方法调用失败:', exportError);
          console.error('【调试】错误详情:', exportError.message);
          console.error('【调试】错误堆栈:', exportError.stack);

          // 导出完成后移除临时表格
          document.body.removeChild(table);
          console.log('【调试】临时表格已移除');
          throw exportError;
        }
      } catch (imgLoadError) {
        console.error('【调试】等待图片加载过程中发生错误:', imgLoadError);
        console.error('【调试】错误详情:', imgLoadError.message);
        console.error('【调试】错误堆栈:', imgLoadError.stack);
        try {
          console.log('【调试】尝试强制导出...');
          table2excel.export(table, fullFileName);
          console.log('【调试】强制导出成功');

          // 导出完成后移除临时表格
          document.body.removeChild(table);
          console.log('【调试】临时表格已移除');
          return true;
        } catch (forceExportError) {
          console.error('【调试】强制导出失败:', forceExportError);
          console.error('【调试】错误详情:', forceExportError.message);
          console.error('【调试】错误堆栈:', forceExportError.stack);

          // 导出完成后移除临时表格
          document.body.removeChild(table);
          console.log('【调试】临时表格已移除');
          throw forceExportError;
        }
      }
    }
  } catch (error) {
    console.error('使用js-table2excel导出Excel失败:', error);
    console.error('错误详情:', error.message);
    console.error('错误堆栈:', error.stack);

    // 尝试使用XLSX导出（不包含图片）
    console.log('尝试使用XLSX导出（不包含图片）...');
    try {
      // 不要调用exportToExcel，直接在这里实现导出逻辑
      console.log('创建工作簿...');
      const wb = XLSX.utils.book_new();

      // 准备数据
      const exportRows = data.map(row => {
        const exportRow = {};
        headers.forEach(header => {
          exportRow[header.text] = row[header.field] || '';
        });
        return exportRow;
      });
      console.log('创建工作表...');
      const ws = XLSX.utils.json_to_sheet(exportRows);

      // 添加工作表到工作簿
      XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');

      // 处理文件名
      const timestamp = new Date().toISOString().replace(/[-:.]/g, '').substring(0, 14);
      const fullFileName = `${fileName}_${timestamp}`;

      // 导出
      console.log('导出Excel文件...');
      const excelData = XLSX.write(wb, {
        bookType: 'xlsx',
        type: 'array'
      });

      // 创建Blob
      const blob = new Blob([excelData], {
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
      });

      // 创建下载链接
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${fullFileName}.xlsx`;

      // 触发下载
      document.body.appendChild(a);
      a.click();

      // 清理
      setTimeout(() => {
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }, 0);
      console.log('XLSX导出成功（不包含图片）');
      return true;
    } catch (xlsxError) {
      console.error('XLSX导出失败:', xlsxError);

      // 最后尝试CSV导出
      console.log('尝试CSV导出...');
      return exportToCSV(data, fileName, headers);
    }
  }
};

/**
 * 将数据导出为Excel文件
 * @param {Array} data - 要导出的数据数组
 * @param {String} fileName - 导出的文件名（不含后缀）
 * @param {Array} headers - 要导出的列标题和对应字段名
 * @param {Object} imageData - 图片数据对象（可选）
 */
export const exportToExcel = (data, fileName, headers, imageData = null) => {
  if (!data || data.length === 0) {
    console.error('导出失败：没有数据');
    return false;
  }

  // 处理文件名
  fileName = fileName.replace(/[\\/:*?"<>|]/g, '_');
  const timestamp = new Date().toISOString().replace(/[-:.]/g, '').substring(0, 14);
  console.log('导出函数被调用，数据条数:', data.length);
  console.log('是否有图片数据:', imageData ? '是' : '否');
  if (imageData) {
    console.log('图片数据条数:', Object.keys(imageData).length);
  }

  // 强制使用XLSX导出Excel（不管是否有图片）
  try {
    console.log('尝试导出Excel...');

    // 创建工作簿
    const wb = XLSX.utils.book_new();
    console.log('工作簿创建成功');

    // 准备数据
    let enhancedData = [];

    // 如果有图片数据，添加图片链接
    if (imageData && Object.keys(imageData).length > 0) {
      enhancedData = data.map((row, index) => {
        const newRow = {
          ...row
        };

        // 检查是否有图片数据
        if (imageData[index]) {
          // 添加图片链接
          if (imageData[index][7] && imageData[index][7].url) {
            newRow['清理前照片'] = imageData[index][7].url;
          }
          if (imageData[index][8] && imageData[index][8].url) {
            newRow['清理后照片'] = imageData[index][8].url;
          }
        }
        return newRow;
      });
    } else {
      enhancedData = data;
    }
    console.log('数据准备完成，开始创建工作表');

    // 创建工作表
    const ws = XLSX.utils.json_to_sheet(enhancedData);
    console.log('工作表创建成功');

    // 设置列宽
    const colWidths = headers.map(() => ({
      wch: 20
    }));
    ws['!cols'] = colWidths;

    // 添加工作表到工作簿
    XLSX.utils.book_append_sheet(wb, ws, '危险废物记录');
    console.log('工作表已添加到工作簿');

    // 导出Excel文件
    const excelFileName = `${fileName}_${timestamp}.xlsx`;
    console.log('准备导出Excel文件:', excelFileName);

    // 使用write方法导出
    console.log('使用XLSX.write方法导出...');
    const wbout = XLSX.write(wb, {
      bookType: 'xlsx',
      type: 'array'
    });
    console.log('Excel数据生成成功，大小:', wbout.length, '字节');

    // 创建Blob对象
    const blob = new Blob([wbout], {
      type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    });
    console.log('Blob创建成功，大小:', blob.size, '字节');

    // 创建下载链接
    const url = URL.createObjectURL(blob);
    console.log('URL创建成功:', url.substring(0, 30) + '...');
    const link = document.createElement('a');
    link.href = url;
    link.download = excelFileName;
    console.log('下载链接创建成功，准备触发点击');

    // 触发下载
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
    console.log('Excel导出成功');
    return true;
  } catch (error) {
    console.error('导出Excel失败，错误详情:', error);
    console.log('回退到CSV导出');
    // 如果Excel导出失败，回退到CSV导出
    exportToCSV(data, fileName, headers);
    return false;
  }
};

/**
 * 导出为CSV文件
 * @param {Array} data - 数据数组
 * @param {String} fileName - 文件名
 * @param {Array} headers - 表头配置
 */
export const exportToCSV = (data, fileName, headers) => {
  // 处理文件名
  const timestamp = new Date().toISOString().replace(/[-:.]/g, '').substring(0, 14);
  const csvFileName = `${fileName}_${timestamp}.csv`;

  // 准备CSV内容
  let csvContent = '\uFEFF'; // 添加BOM以支持中文

  // 添加表头
  const headerRow = headers.map(h => `"${h.title}"`).join(',');
  csvContent += headerRow + '\r\n';

  // 添加数据行
  data.forEach(item => {
    const row = headers.map(header => {
      const value = item[header.field];

      // 处理null和undefined
      if (value === null || value === undefined) {
        return '';
      }

      // 处理数字
      if (header.type === 'number' || typeof value === 'number') {
        return value;
      }

      // 处理字符串，特别是包含逗号、引号或换行符的字符串
      let cellValue = String(value);
      if (cellValue.includes(',') || cellValue.includes('"') || cellValue.includes('\n')) {
        cellValue = cellValue.replace(/"/g, '""'); // 将引号替换为两个引号
        cellValue = `"${cellValue}"`; // 用引号包裹整个值
      }
      return cellValue;
    }).join(',');
    csvContent += row + '\r\n';
  });

  // 创建下载链接
  const blob = new Blob([csvContent], {
    type: 'text/csv;charset=utf-8;'
  });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.setAttribute('href', url);
  link.setAttribute('download', csvFileName);
  link.style.visibility = 'hidden';
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
  URL.revokeObjectURL(url);
  console.log('CSV导出成功');
};

/**
 * 准备图片数据用于导出
 * @param {Array} records - 记录数据
 * @param {String} baseUrl - 基础URL
 * @returns {Object} 图片数据对象
 */
export const prepareImageExportData = async (records, baseUrl) => {
  const imageData = {};
  try {
    console.log('准备图片数据，记录数:', records.length);
    console.log('基础URL:', baseUrl);

    // 处理每条记录的图片
    records.forEach((record, index) => {
      imageData[index] = {
        beforeImages: [],
        afterImages: []
      };

      // 处理清理前照片
      if (record.photo_path_before) {
        console.log(`记录 ${index} 清理前照片路径:`, record.photo_path_before);
        let beforePaths = [];
        try {
          // 尝试解析JSON
          if (typeof record.photo_path_before === 'string' && record.photo_path_before.startsWith('[')) {
            beforePaths = JSON.parse(record.photo_path_before);
            console.log(`记录 ${index} 解析后的清理前照片路径:`, beforePaths);
          } else {
            beforePaths = [record.photo_path_before];
          }
        } catch (e) {
          console.error('解析清理前照片路径失败:', e);
          beforePaths = [record.photo_path_before];
        }

        // 添加完整URL
        imageData[index].beforeImages = beforePaths.map(path => {
          // 确保路径是字符串
          const pathStr = String(path);
          // 如果路径已经是完整URL，则直接返回
          if (pathStr.startsWith('http://') || pathStr.startsWith('https://')) {
            return pathStr;
          }
          // 否则拼接基础URL
          return `${baseUrl}${pathStr}`;
        });
        console.log(`记录 ${index} 完整的清理前照片URL:`, imageData[index].beforeImages);
      }

      // 处理清理后照片
      if (record.photo_path_after) {
        console.log(`记录 ${index} 清理后照片路径:`, record.photo_path_after);
        let afterPaths = [];
        try {
          // 尝试解析JSON
          if (typeof record.photo_path_after === 'string' && record.photo_path_after.startsWith('[')) {
            afterPaths = JSON.parse(record.photo_path_after);
            console.log(`记录 ${index} 解析后的清理后照片路径:`, afterPaths);
          } else {
            afterPaths = [record.photo_path_after];
          }
        } catch (e) {
          console.error('解析清理后照片路径失败:', e);
          afterPaths = [record.photo_path_after];
        }

        // 添加完整URL
        imageData[index].afterImages = afterPaths.map(path => {
          // 确保路径是字符串
          const pathStr = String(path);
          // 如果路径已经是完整URL，则直接返回
          if (pathStr.startsWith('http://') || pathStr.startsWith('https://')) {
            return pathStr;
          }
          // 否则拼接基础URL
          return `${baseUrl}${pathStr}`;
        });
        console.log(`记录 ${index} 完整的清理后照片URL:`, imageData[index].afterImages);
      }
    });
    console.log('图片数据准备完成，总记录数:', Object.keys(imageData).length);
    return imageData;
  } catch (error) {
    console.error('准备图片数据失败:', error);
    return {};
  }
};

