              resolve(); // 即使失败也继续导出
            };
            
            // 添加超时处理
            setTimeout(() => {
              if (!img.complete) {
                console.error(`【调试】图片 ${imgIndex + 1} 加载超时`);
                resolve(); // 超时也继续导出
              }
            }, 5000); // 5秒超时
          }
        });
      });
      
      // 等待所有图片加载完成或超时
      try {
        await Promise.all(imgPromises);
        console.log('【调试】所有图片加载完成或超时，开始导出');
        
        // 再次检查图片状态
