import { ref, onMounted, computed, reactive } from 'vue';
import { useRouter } from 'vue-router';
import { ElMessage, ElMessageBox, ElImageViewer } from 'element-plus';
import httpService from '../config/httpService';
import apiConfig from '../config/api';
import { ArrowLeft, Home, Refresh, Plus, User, ArrowDown, ArrowUp, Download, Loading } from '@element-plus/icons-vue';
import { exportToExcelWithImages } from '../utils/exportUtils';
import auth from '../store/auth';
export default {
  name: 'RecordsList',
  components: {
    ArrowLeft,
    Home,
    Refresh,
    Plus,
    User,
    ArrowDown,
    ArrowUp,
    Download,
    ElImageViewer,
    Loading
  },
  props: {
    unitId: {
      type: [String, Number],
      required: true
    }
  },
  setup(props) {
    const router = useRouter();
    const records = ref([]);
    const loading = ref(false);
    const unitName = ref('');
    const wasteTypes = ref([]);
    const showFilterPanel = ref(true);

    // 添加baseUrl定义
    const baseUrl = apiConfig.baseURL;

    // 添加解析照片路径的函数
    const parsePhotoPath = path => {
      if (!path) return [];
      try {
        // 尝试解析为JSON
        if (path.startsWith('[') && path.endsWith(']')) {
          return JSON.parse(path);
        }
        // 如果不是JSON格式，则将其作为单个路径返回
        return [path];
      } catch (error) {
        console.error('解析照片路径失败:', error);
        // 如果解析失败，将其作为单个路径返回
        return [path];
      }
    };

    // 添加分页相关的响应式变量
    const page = ref(1);
    const pageSize = ref(20);
    const hasMore = ref(true);
    const loadingMore = ref(false);

    // 筛选表单
    const filterForm = reactive({
      dateRange: null,
      wasteTypeId: null,
      minQuantity: null,
      maxQuantity: null,
      location: ''
    });

    // 过滤后的记录
    const filteredRecords = computed(() => {
      if (!records.value || !Array.isArray(records.value)) {
        return [];
      }
      return records.value.filter(record => {
        // 检查废物类型
        if (filterForm.wasteTypeId && record.waste_type_id !== filterForm.wasteTypeId) {
          return false;
        }

        // 检查数量范围
        if (filterForm.minQuantity !== null && parseFloat(record.quantity) < filterForm.minQuantity) {
          return false;
        }
        if (filterForm.maxQuantity !== null && parseFloat(record.quantity) > filterForm.maxQuantity) {
          return false;
        }

        // 检查地点关键词
        if (filterForm.location && !record.location.toLowerCase().includes(filterForm.location.toLowerCase())) {
          return false;
        }

        // 检查日期范围
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          const startDate = new Date(filterForm.dateRange[0]);
          const endDate = new Date(filterForm.dateRange[1]);
          endDate.setHours(23, 59, 59); // 设置为当天结束时间

          const recordDate = new Date(record.collection_start_time);
          if (recordDate < startDate || recordDate > endDate) {
            return false;
          }
        }
        return true;
      });
    });

    // 判断是否为管理员
    const isAdmin = computed(() => {
      return auth.isAdmin();
    });

    // 判断是否为单位管理员
    const isUnitAdmin = computed(() => {
      return auth.isUnitAdmin();
    });

    // 格式化日期时间
    const parseFormattedDateTime = dateTimeStr => {
      if (!dateTimeStr) return '';
      const date = new Date(dateTimeStr);
      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')} ${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}`;
    };

    // 刷新记录
    const refreshRecords = async () => {
      await fetchRecords();
      ElMessage.success('记录已刷新');
    };

    // 返回填报页面
    const goBack = () => {
      // 获取当前用户的单位ID
      const unitId = auth.getUnitId();
      if (unitId) {
        router.push(`/unit/${unitId}`);
      } else {
        // 如果没有单位ID，则返回首页
        router.push('/');
      }
    };

    // 返回首页
    const goHome = () => {
      router.push('/');
    };

    // 前往人员管理
    const goToUserManagement = () => {
      router.push(`/user-management`);
    };

    // 添加新记录
    const addNewRecord = () => {
      router.push(`/unit/${props.unitId}`);
    };

    // 编辑记录
    const editRecord = recordId => {
      router.push(`/record/${recordId}`);
    };

    // 判断是否可以编辑记录
    const canEdit = record => {
      if (!record) return false;

      // 超级管理员可以编辑所有记录
      if (isAdmin.value) return true;

      // 单位管理员可以编辑本单位的记录
      const unitId = auth.getUnitId();
      if (isUnitAdmin.value && unitId && record.unit_id === parseInt(unitId)) return true;

      // 普通用户只能编辑自己创建的记录
      const userId = auth.getUserId();
      return userId && record.creator_id === parseInt(userId);
    };

    // 确认删除
    const confirmDelete = record => {
      ElMessageBox.confirm(`确定要删除这条废物记录吗？此操作不可逆。`, '删除确认', {
        confirmButtonText: '确定删除',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await httpService.delete(`${apiConfig.endpoints.wasteRecords}/${record.id}`);
          ElMessage.success('记录已成功删除');
          await fetchRecords(); // 重新加载记录
        } catch (error) {
          console.error('删除记录失败:', error);
          ElMessage.error('删除记录失败，请重试');
        }
      }).catch(() => {
        ElMessage.info('已取消删除');
      });
    };

    // 应用筛选
    const applyFilter = async () => {
      try {
        // 重置分页
        page.value = 1;

        // 重新获取记录
        await fetchRecords();
        ElMessage.success('筛选条件已应用');
      } catch (error) {
        console.error('应用筛选失败:', error);
        ElMessage.error('应用筛选失败');
      }
    };

    // 重置筛选
    const resetFilter = async () => {
      filterForm.dateRange = null;
      filterForm.wasteTypeId = null;
      filterForm.minQuantity = null;
      filterForm.maxQuantity = null;
      filterForm.location = '';

      // 重置分页
      page.value = 1;

      // 重新获取记录
      await fetchRecords();
      ElMessage.success('筛选条件已重置');
    };

    // 导出记录
    const exportRecords = async () => {
      try {
        loading.value = true;
        console.log('========== 【调试】开始导出记录 ==========');
        console.log('【调试】使用exportToExcelWithImages函数导出');

        // 获取要导出的记录
        const recordsToExport = filteredRecords.value;
        console.log(`【调试】导出记录数量: ${recordsToExport.length}`);
        if (recordsToExport.length > 0) {
          console.log('【调试】第一条记录ID:', recordsToExport[0].id);
          console.log('【调试】第一条记录示例:', JSON.stringify(recordsToExport[0]).substring(0, 300) + '...');

          // 检查照片字段
          console.log('【调试】第一条记录照片字段:');
          console.log('【调试】photo_path_before:', recordsToExport[0].photo_path_before);
          console.log('【调试】photo_path_before类型:', typeof recordsToExport[0].photo_path_before);
          console.log('【调试】photo_path_after:', recordsToExport[0].photo_path_after);
          console.log('【调试】photo_path_after类型:', typeof recordsToExport[0].photo_path_after);
        }
        if (recordsToExport.length === 0) {
          ElMessage.warning('没有可导出的记录');
          loading.value = false;
          return;
        }

        // 设置导出基础URL
        // 使用完整的基础URL，包括协议、主机名和端口
        const exportBaseUrl = window.location.origin;
        console.log('【调试】导出基础URL:', exportBaseUrl);
        console.log('【调试】当前window.location:', window.location.href);
        console.log('【调试】当前window.location.origin:', window.location.origin);

        // 准备导出数据，并保存原始记录
        console.log('【调试】开始准备导出数据...');
        const exportData = recordsToExport.map((record, index) => {
          console.log(`【调试】准备第 ${index + 1}/${recordsToExport.length} 条记录数据...`);

          // 检查照片路径
          if (record.photo_path_before) {
            console.log(`【调试】记录 ${index} 清理前照片路径:`, record.photo_path_before);
            console.log(`【调试】解析后的照片路径:`, parsePhotoPath(record.photo_path_before));
          }
          if (record.photo_path_after) {
            console.log(`【调试】记录 ${index} 清理后照片路径:`, record.photo_path_after);
            console.log(`【调试】解析后的照片路径:`, parsePhotoPath(record.photo_path_after));
          }
          const exportRow = {
            '单位': record.unit_name || '',
            '废物类型': record.waste_type_name || '',
            '收集地点': record.location || '',
            '收集时间': record.collection_start_time ? new Date(record.collection_start_time).toLocaleString() : '',
            '数量(kg)': record.quantity || 0,
            '记录时间': record.created_at ? new Date(record.created_at).toLocaleString() : '',
            '汇报人': record.creator_name || '未知',
            '清理前照片': '',
            '清理后照片': '',
            '__original': record // 保存原始记录
          };
          console.log(`【调试】记录 ${index} 导出行数据:`, JSON.stringify(exportRow).substring(0, 200) + '...');
          return exportRow;
        });
        console.log('【调试】导出数据准备完成');
        console.log('【调试】导出数据条数:', exportData.length);
        console.log('【调试】导出数据示例:', JSON.stringify(exportData[0]).substring(0, 300) + '...');

        // 导出文件
        const fileName = `${unitName.value}危险废物记录`;
        console.log('【调试】导出文件名:', fileName);
        const headers = [{
          text: '单位',
          field: '单位'
        }, {
          text: '废物类型',
          field: '废物类型'
        }, {
          text: '收集地点',
          field: '收集地点'
        }, {
          text: '收集时间',
          field: '收集时间'
        }, {
          text: '数量(kg)',
          field: '数量(kg)'
        }, {
          text: '记录时间',
          field: '记录时间'
        }, {
          text: '汇报人',
          field: '汇报人'
        }, {
          text: '清理前照片',
          field: '清理前照片'
        }, {
          text: '清理后照片',
          field: '清理后照片'
        }];
        console.log('【调试】导出表头:', JSON.stringify(headers));

        // 显示提示信息，告知用户正在处理图片
        ElMessage.info('正在处理图片，请稍候...');
        console.log('【调试】开始导出Excel文件...');
        console.log('【调试】直接调用exportToExcelWithImages函数');

        // 直接调用exportToExcelWithImages函数，不使用其他导出函数
        try {
          console.log('【调试】调用exportToExcelWithImages前');
          const result = await exportToExcelWithImages(exportData, fileName, headers);
          console.log('【调试】调用exportToExcelWithImages后，结果:', result);
          if (result) {
            console.log('【调试】导出成功');
            ElMessage.success('导出成功');
          } else {
            console.log('【调试】导出失败，已回退到CSV导出');
            ElMessage.warning('Excel导出失败，已回退到CSV导出');
          }
        } catch (exportError) {
          console.error('【调试】导出过程中发生错误:', exportError);
          console.error('【调试】错误详情:', exportError.message);
          console.error('【调试】错误堆栈:', exportError.stack);
          ElMessage.error('导出失败: ' + exportError.message);
        }
      } catch (error) {
        console.error('【调试】导出过程中发生错误:', error);
        console.error('【调试】错误详情:', error.message);
        console.error('【调试】错误堆栈:', error.stack);
        ElMessage.error('导出失败: ' + error.message);
      } finally {
        console.log('========== 【调试】导出记录结束 ==========');
        loading.value = false;
      }
    };

    // 图片预览相关
    const showViewer = ref(false);
    const previewImages = ref([]);
    const previewIndex = ref(0);

    // 预览照片
    const previewPhoto = (paths, index) => {
      // 确保paths是数组
      const photoArray = Array.isArray(paths) ? paths : [paths];
      previewImages.value = photoArray.map(path => `${baseUrl}${path}`);
      previewIndex.value = index || 0;
      showViewer.value = true;
    };

    // 关闭预览
    const closeViewer = () => {
      showViewer.value = false;
    };
    onMounted(async () => {
      await fetchUnitName();
      await fetchWasteTypes();
      await fetchRecords();
    });

    // 获取废物类型列表
    const fetchWasteTypes = async () => {
      try {
        const response = await httpService.get(apiConfig.endpoints.wasteTypes);
        wasteTypes.value = response.data;
      } catch (error) {
        console.error('Error fetching waste types:', error);
        ElMessage.error('获取废物类型列表失败');
      }
    };
    const fetchUnitName = async () => {
      try {
        const response = await httpService.get(apiConfig.endpoints.units);
        const unit = response.data.find(u => u.id === parseInt(props.unitId));
        if (unit) {
          unitName.value = unit.name;
        }
      } catch (error) {
        console.error('Error fetching unit name:', error);
        ElMessage.error('获取单位信息失败');
      }
    };
    const fetchRecords = async (isLoadMore = false) => {
      if (!isLoadMore) {
        loading.value = true;
        page.value = 1;
        records.value = [];
      } else {
        loadingMore.value = true;
      }
      try {
        // 基本参数
        const params = {
          page: page.value,
          pageSize: pageSize.value
        };

        // 添加筛选参数
        if (filterForm.wasteTypeId) params.wasteTypeId = filterForm.wasteTypeId;
        if (filterForm.minQuantity !== null) params.minQuantity = filterForm.minQuantity;
        if (filterForm.maxQuantity !== null) params.maxQuantity = filterForm.maxQuantity;
        if (filterForm.location) params.location = filterForm.location;
        if (filterForm.dateRange && filterForm.dateRange.length === 2) {
          params.dateRange = JSON.stringify(filterForm.dateRange);
        }

        // 获取当前用户ID
        const userId = auth.getUserId();
        if (!userId) {
          throw new Error('用户未登录');
        }

        // 使用用户ID获取记录
        const response = await httpService.get(`${apiConfig.endpoints.wasteRecordsByUser}/${userId}`, {
          params
        });

        // 确保响应数据包含records字段，并且是一个数组
        if (response.data && Array.isArray(response.data.records)) {
          if (isLoadMore) {
            records.value = [...records.value, ...response.data.records];
          } else {
            records.value = response.data.records;
          }
          hasMore.value = !!response.data.hasMore;
        } else {
          console.error('Invalid response format:', response.data);
          records.value = [];
          hasMore.value = false;
          ElMessage.error('获取废物记录失败：数据格式错误');
        }
      } catch (error) {
        console.error('Error fetching records:', error);
        records.value = [];
        hasMore.value = false;
        ElMessage.error('获取废物记录失败');
      } finally {
        loading.value = false;
        loadingMore.value = false;
      }
    };

    // 处理滚动加载更多
    const handleScroll = async e => {
      // 修复解构赋值问题，确保e.target存在且包含所需属性
      if (!e || !e.target) return;
      const scrollHeight = e.target.scrollHeight || 0;
      const scrollTop = e.target.scrollTop || 0;
      const clientHeight = e.target.clientHeight || 0;

      // 当滚动到底部时加载更多
      if (scrollHeight - scrollTop - clientHeight < 50 && hasMore.value && !loadingMore.value) {
        page.value++;
        await fetchRecords(true);
      }
    };
    return {
      records,
      filteredRecords,
      loading,
      unitName,
      wasteTypes,
      isAdmin,
      isUnitAdmin,
      parseFormattedDateTime,
      refreshRecords,
      goBack,
      goHome,
      goToUserManagement,
      addNewRecord,
      editRecord,
      canEdit,
      confirmDelete,
      apiConfig,
      // 筛选相关
      showFilterPanel,
      filterForm,
      applyFilter,
      resetFilter,
      // 导出相关
      exportRecords,
      // 图片预览相关
      showViewer,
      previewImages,
      previewIndex,
      previewPhoto,
      closeViewer,
      handleScroll,
      // 照片路径解析
      parsePhotoPath,
      baseUrl,
      // 分页相关
      page,
      pageSize,
      hasMore,
      loadingMore
    };
  }
};
